// src/pages/Admin/AdminDashboard.js
import React, { useState } from 'react';
import { Link, Outlet, useLocation } from 'react-router-dom';

const AdminDashboard = () => {
  const [showServicesSubMenu, setShowServicesSubMenu] = useState(false);
  const location = useLocation();

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <div className="w-1/4 bg-gray-800 text-white p-4">
        <h2 className="text-2xl font-bold mb-6">Admin Panel</h2>
        <ul>
          <li className={`mb-4 ${location.pathname.includes('/admin/users') ? 'text-blue-300' : ''}`}>
            <Link to="/admin/users" className="text-lg hover:text-gray-300">Manage Users</Link>
          </li>
          <li className={`mb-4 ${location.pathname.includes('/admin/bookings') ? 'text-green-300' : ''}`}>
            <Link to="/admin/bookings" className="text-lg hover:text-gray-300">Manage Bookings</Link>
          </li>
          <li className="mb-4">
            <button
              onClick={() => setShowServicesSubMenu(!showServicesSubMenu)}
              className="text-lg hover:text-gray-300 focus:outline-none"
            >
              Manage Services
            </button>
            {/* Sub-menus for Services */}
            {showServicesSubMenu && (
              <ul className="ml-4 mt-2">
                <li className={`mb-2 ${location.pathname.includes('/admin/services/updating') ? 'text-purple-300' : ''}`}>
                  <Link to="/admin/services/updating" className="text-lg hover:text-gray-300">Services Updating</Link>
                </li>
                <li className={`mb-2 ${location.pathname.includes('/admin/services/holidays') ? 'text-purple-300' : ''}`}>
                  <Link to="/admin/services/holidays" className="text-lg hover:text-gray-300">Holidays</Link>
                </li>
                <li className={`mb-2 ${location.pathname.includes('/admin/services/working-hours') ? 'text-purple-300' : ''}`}>
                  <Link to="/admin/services/working-hours" className="text-lg hover:text-gray-300">Working Hours</Link>
                </li>
              </ul>
            )}
          </li>
        </ul>
      </div>

      {/* Content Area */}
      <div className="w-3/4 bg-white p-6">
        <Outlet />
      </div>
    </div>
  );
};

export default AdminDashboard;
